query.sql:
SELECT &ifp_equipment;.*, &ifp_equipment;_type.name, &ifp_locations;.location_id, uplink_location_equipment.equipment_id, uplink_location_equipment.location_equipment_id, &ifp_equipment_type_images;.*
FROM &ifp_equipment;, &ifp_equipment;_type, &ifp_locations;, uplink_location_equipment, &ifp_equipment_type_images;
WHERE &ifp_equipment;.deletion_datetime > ? AND &ifp_equipment;.equipment_type_id=&ifp_equipment;_type.equipment_type_id AND &ifp_equipment;.equipment_id = ? AND &ifp_locations;.deletion_datetime > ? AND uplink_location_equipment.deletion_datetime > ? AND uplink_location_equipment.equipment_id = ? AND uplink_location_equipment.location_id = &ifp_locations;.location_id AND &ifp_equipment_type_images;.equipment_type_id = &ifp_equipment;_type.equipment_type_id AND &ifp_equipment_type_images;.deletion_datetime > ? AND &ifp_equipment;_type.deletion_datetime > ?
